resource "aws_ecr_registry_scanning_configuration" "<example_resource_name>" {
  scan_type = "ENHANCED"

  rule {
    scan_frequency = "SCAN_ON_PUSH"  # Ensures scan on push
    repository_filter {
      filter      = "*"               # Applies to all repositories
      filter_type = "WILDCARD"
    }
  }
}
