resource search 'Microsoft.Search/searchServices@2023-11-01' = {
  name: '<example_resource_name>'
  location: '<location>'
  sku: { name: 'basic' }
  properties: {
    publicNetworkAccess: 'disabled' // CRITICAL: Disables public access so the service is not reachable from the internet
  }
}
