// Enable Defender for Containers image vulnerability scanning at subscription scope
targetScope = 'subscription'

resource containersPricing 'Microsoft.Security/pricings@2023-01-01' = {
  name: 'Containers'
  properties: {
    pricingTier: 'Standard'
    extensions: [
      {
        name: 'ContainerRegistriesVulnerabilityAssessments' // CRITICAL: enables ACR image vulnerability scanning
        isEnabled: true                                   // CRITICAL: turns the extension ON
      }
    ]
  }
}
