// Enable Key Vault diagnostic settings with audit + allLogs
param keyVaultName string
param workspaceId string

resource kv 'Microsoft.KeyVault/vaults@2023-07-01' existing = {
  name: keyVaultName
}

resource diag 'Microsoft.Insights/diagnosticSettings@2021-05-01-preview' = {
  name: '<example_resource_name>'
  scope: kv
  properties: {
    workspaceId: workspaceId
    logs: [
      {
        categoryGroup: 'audit'   // critical: enables audit logs
        enabled: true            // required to pass the check
      }
      {
        categoryGroup: 'allLogs' // critical: enables allLogs group
        enabled: true            // required to pass the check
      }
    ]
  }
}
```
