// Update the existing firewall rule that allowed Azure services (0.0.0.0) to a specific IP/range
resource fwRule 'Microsoft.DBforPostgreSQL/flexibleServers/firewallRules@2023-03-01-preview' = {
  name: '<example_server_name>/<example_rule_name>'
  properties: {
    startIpAddress: '<START_IP>' // critical: not 0.0.0.0; disables "Allow Azure services"
    endIpAddress: '<END_IP>'     // critical: not 0.0.0.0
  }
}
