Code.Terraform:
resource "cloudflare_ruleset" "rate_limit" {
  zone_id = var.zone_id
  name    = "Rate limiting"
  kind    = "zone"
  phase   = "http_ratelimit"
  rules {
    action = "block"
    ratelimit {
      characteristics = ["ip.src"]
      period         = 60
      requests_per_period = 100
      mitigation_timeout = 600
    }
    expression  = "true"
    description = "Rate limit all requests"
  }
}
