Code.Terraform:
resource "oci_events_rule" "user_auth_rule" {
  display_name    = "user-authentication-events"
  is_enabled      = true
  compartment_id  = var.tenancy_ocid
  condition       = "{\"eventType\":[\"com.oraclecloud.identitysignon.interactivelogin\"]}"
  actions {
    actions {
      action_type = "ONS"
      is_enabled  = true
      topic_id    = oci_ons_notification_topic.topic.id
    }
  }
}
